var h1=document.getElementsByTagName("h1"),
    seconds=0,
    minutes=0,
    hours=0,
    start=document.getElementById("start"),
    stop=document.getElementById("stop"),
    reset=document.getElementById("reset"),
    temp;

function repeat(){
    temp=setTimeout(add,1000);
}
function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}
function add() {
    seconds++;
    if(seconds>=60){
        seconds=0;
        minutes++;
        if(minutes>=60){
            minutes=0;
            hours++;
            beep();
        }
    }
    h1[1].innerHTML=(hours%10==hours?"0"+hours:hours)+":"+(minutes%10==minutes?"0"+minutes:minutes)+":"+(seconds%10==seconds?"0"+seconds:seconds);
    repeat();
}
function resetit(){
    h1[1].innerHTML="00:00:00";
    seconds=0;
    hours=0;
    minutes=0;
}
start.addEventListener("click",repeat);
stop.addEventListener("click",function () {
    clearTimeout(temp);
});
reset.addEventListener("click",resetit);

var start1=document.getElementById("start1"),
    stop1=document.getElementById("stop1"),
    seconds1,
    temp1;
 var minutes1;
start1.addEventListener("click",function() {
    clearTimeout(temp1);
    seconds1=60;
    minutes1=document.getElementById("timermin").value;
    h1[4].innerHTML=(minutes1%10==minutes1?"0"+minutes1:minutes1)+":00";
    minutes1-=1;
    subtract();
});
function subtract() {
    seconds1--;
    if(seconds1<0){
        seconds1=59;
        minutes1--;
    }
    if(minutes1<0){
        temp=setTimeout(beep,1000);
    }
    h1[4].innerHTML=(minutes1%10==minutes1?"0"+minutes1:minutes1)+":"+(seconds1%10==seconds1?"0"+seconds1:seconds1);
    repeatit();
}
function repeatit() {
    temp1=setTimeout(subtract,1000);
}
stop1.addEventListener("click",function () {
    clearTimeout(temp1);
});